mapper.insert:
 INSERT INTO RECYCLE_BOARD(BOARD_NO,
                         RECY_BOARD_TITLE,
                        MEMBER_NO,
                     RECY_BOARD_CONTENT
    
              )
              VALUES(#{boardNo},
                     #{recycleBoardTitle},
                     '1',
                     #{recycleBoardContent}
              )

    INSERT INTO BOARD_ATTACHMENT
  				      ( 
  				          BO_FILE_NO
						, BOARD_NO
						, BOARD_TYPE
						, BO_ORIGIN_NAME
						, BO_CHANGE_NAME
						, FILE_PATH

  				      )
  			VALUES   (
  			  			#{boFileNo}
  					  , #{boardNo}
  					  , '3'
  					  , #{boOriginName}
  					  , #{boChangeName}
  					  , #{filePath}
  					 )

mapper.update:
  		UPDATE BOARD_ATTACHMENT
		SET BO_ORIGIN_NAME = #{boOriginName}
		  , BO_CHANGE_NAME = #{boChangeName}
		  , FILE_PATH = #{filePath}
		  , CREATE_DATE = SYSDATE
		WHERE BO_FILE_NO = #{boFileNo} 
		AND BOARD_NO = #{boardNo}
		AND STATUS = 'Y'
		AND BOARD_TYPE = 3

 		UPDATE RECYCLE_BOARD
 			SET RECY_BOARD_TITLE = #{recycleBoardTitle}
 			  , RECY_BOARD_CONTENT = #{recycleBoardContent}

 			WHERE BOARD_NO = #{boardNo}
