		 					#{ecoBoardNo}
		 					, 1
		 					, #{brandNo}
		 					, #{ecoBoardTitle}
		 					, #{ecoBoardContent}
		 				)

mapper.select:
SELECT BRAND_NO
FROM BRAND
WHERE BRAND_NAME = #{brandName}

mapper.update:
UPDATE SEARCHECO_BOARD
SET ECO_BOARD_TITLE = #{ecoBoardTitle}
   ,ECO_BOARD_CONTENT = #{ecoBoardContent}
WHERE ECO_BOARD_NO = #{ecoBoardNo}

mapper.insert:
  			INSERT INTO ECO_LIKE
  				( MEMBER_NO
				, ECO_BOARD_NO
				 )
  			VALUES
  				( #{memberNo}
  				, #{ecoBoardNo}
  				 )

mapper.update:
UPDATE SEARCHECO_BOARD
SET LIKE_COUNT = LIKE_COUNT + 1
WHERE ECO_BOARD_NO = #{ecoBoardNo}

mapper.select:
			SELECT LIKE_COUNT
			FROM SEARCHECO_BOARD
			WHERE ECO_BOARD_NO = #{ecoBoardNo}

mapper.delete:
DELETE FROM ECO_LIKE
WHERE ECO_BOARD_NO = #{ecoBoardNo}
AND MEMBER_NO=#{memberNo}

mapper.update:
UPDATE SEARCHECO_BOARD
SET LIKE_COUNT = LIKE_COUNT - 1
WHERE ECO_BOARD_NO = #{ecoBoardNo}

mapper.select:
  		SELECT COUNT(*)
	    FROM ECO_LIKE
	    WHERE ECO_BOARD_NO = #{ecoBoardNo}
	    AND MEMBER_NO = #{memberNo}

SELECT ECO_BOARD_NO
FROM SEARCHECO_BOARD
WHERE BRAND_NO = #{boardNo}
