mapper.select:
  		SELECT MEMBER_ID
		FROM MEMBER
		WHERE MEMBER_NAME = #{memberName}
		AND EMAIL = #{email}

  		SELECT *
		FROM MEMBER
		WHERE MEMBER_ID = #{memberId}
		AND MEMBER_NAME = #{memberName}
		AND EMAIL = #{email}
		AND STATUS = 'Y'

mapper.insert:

	
        SELECT SEQ_MNO.NEXTVAL
        FROM DUAL
    
	
  		INSERT INTO MEMBER(MEMBER_NO
		                  ,MEMBER_ID
		                  ,MEMBER_PWD
		                  ,MEMBER_NAME
		                  ,NICKNAME
		                  ,BIRTH
		                  ,GENDER
		                  ,EMAIL
		                  ,PHONE)
            VALUES(#{memberNo}
                  ,#{memberId}
                  ,#{memberPwd}
                  ,#{memberName}
                  ,#{nickName}
                  ,#{birth}
                  ,#{gender}
                  ,#{email}
                  ,#{phone})

mapper.update:
UPDATE MEMBER
SET MEMBER_PWD = #{memberPwd}
WHERE MEMBER_ID = #{memberId}
AND STATUS = 'Y'

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM MEMBER
WHERE NICKNAME = #{nickName}

SELECT COUNT(*)
FROM MEMBER
WHERE EMAIL = #{email}

SELECT COUNT(*)
FROM MEMBER
WHERE PHONE = #{phone}
